import './css/normalize.css';
import './css/bootstrap.min.css';
import './css/mdb.min.css';
import './App.css';
import React, { useState, useEffect } from 'react';
import { useNavigate, Route, Routes} from 'react-router-dom';
//import styled from 'styled-components';

function Works() {
    let data = require("./json/portfolio.json");
    const [isMobile, setIsMobile] = useState(true);

    //sort date according to date
    const sortedData = data.sort((a, b) => Date.parse(a.date_end) > Date.parse(b.date_end) ? -1 : 1);
    const items = sortedData.filter((item) => item.title !== "null");

    const mobileComponent = () => (
        <div id='works_area'>
            {
                items.map((item, index) => {
                    let link = !(item.link == '#');
                    return (
                        <div key={index} id={`work${index}`} className="container align-items-center card default-gradient">
                            <div className="row">
                                <img className="work_pic" src={process.env.PUBLIC_URL + '/' + item.image_dir} />
                            </div>
                            <div className="row justify-content-center">
                                <h1 className="title">{item.title}</h1>
                                <p className="description" dangerouslySetInnerHTML={{ __html: item.description }}></p>
                                {link && <a className="description desc_button" href={item.link} target="_blank">前往查看 {">"}</a>}
                            </div>
                        </div>
                    )
                })
            }
        </div>
    );

    const desktopComponent = () => (
        <div id='works_area'>
            {
                items.map((item, index) => {
                    let link = !(item.link == '#');
                    return (
                        <div key={index} id={`work${index}`} className="container card default-gradient">
                            <div className="row align-items-start">
                                <div className="col-md-6 wow bounceInLeft">
                                    <img className="work_pic" src={process.env.PUBLIC_URL + '/' + item.image_dir} loading="lazy" />
                                    <p className="item_description">{`(${item.date_start}-${item.date_end})`}</p>
                                </div>
                                <div className="col-md-6 wow bounceInRight">
                                    <h1 className="title">{item.title}</h1>
                                    <p className="description" dangerouslySetInnerHTML={{ __html: item.description }}></p>
                                    {link && <a className="description desc_button" href={item.link} target="_blank">前往查看 {">"}</a>}
                                </div>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    );

    useEffect(() => {
        function updateResize() {
            if (window.innerWidth < 768)
                setIsMobile(true);
            else setIsMobile(false);
        }
        updateResize();
        window.addEventListener("resize", updateResize);
    }, [])

    return (
        isMobile ? (
            <div>
                {mobileComponent()}
            </div>
        ) : (
            <div>
                {desktopComponent()}
            </div>
        )
    );
}

function Navbar() {
    const [isOpen, setIsOpen] = useState(false);
    let buttonHandler = () => setIsOpen(!isOpen);

    //const navigate = useNavigate();
    // fetch("https://ipinfo.io", {
    //     headers: {
    //         'Content-Type': 'application/json',
    //         'Accept': 'application/json'
    //     }
    // }).then(response => response.json())
    //     .then(data => {
    //         console.log(data);
    //         if (data.status !== 429) {
    //             document.getElementById("location").innerText = data.country;
    //         } else {
    //             document.getElementById("location").innerText = 'TW';
    //         }
    //     });

    return (
        <nav className="navbar navbar-expand-lg navbar-dark navbar-color fixed-top">
            <button className="navbar-toggler third-button" type="button" data-toggle="collapse"
                data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false"
                aria-label="Toggle navigation" onClick={buttonHandler}>
                <div className={isOpen ? "animated-icon2 open" : "animated-icon3"}><span></span><span></span><span></span></div>
            </button>

            <a className="navbar-brand" href={process.env.PUBLIC_URL}><img src={process.env.PUBLIC_URL + "/img/nav_logo.png"} alt="logo" /></a>

            {/* <!--網頁選單--> */}
            <div className={isOpen ? "collapse navbar-collapse show" : "collapse navbar-collapse"} id="navbarSupportedContent">
                <div className="container">
                    <div className="row">
                        {/* <!--top icon--> */}
                        <div className={isOpen ? "d-none" : "col navbar-nav nav-icon justify-content-end"}>
                            <div className="row">
                                <img className="col" src={process.env.PUBLIC_URL + "/img/icon_where.png"} alt="location" />
                                <p id="location" className="col text-center">TW</p>
                            </div>
                        </div>
                        <div className="w-100"></div>
                        {/* <!--導航選單--> */}
                        <ul className="col navbar-nav ml-auto justify-content-end">
                            <li className="nav-item"><a id="home_link" className="nav-link">首頁</a></li>
                            <li className="nav-item"><a id="portfolio_link" className="nav-link">作品集</a></li>
                            <li className="nav-item"><a id="certificate_link" className="nav-link"
                                href="certificate.html">證書集</a></li>
                            <li className="nav-item"><a id="contact_link" className="nav-link">聯絡方式</a></li>
                        </ul>
                    </div>
                </div>
            </div>
        </nav>
    );
}



export default function App() {
    return (
        <>
            <Navbar />
            <Profile />
            <Offset100vh />
            <Works />
        </>
    );
}
