import './css/normalize.css';
import './css/bootstrap.min.css';
import './css/mdb.min.css';
import './App.css';
import React, { useState, useEffect, Fragment } from 'react';
//import styled from 'styled-components';

async function readJson(dir) {
    try {
        let data = await fetch(dir);
        data = await data.json();
        return data;
    } catch (error) {
        console.log(error);
    }
}

function Works() {
    let data = require("./json/portfolio.json");
    const [isMobile, setIsMobile] = useState(true);

    //sort date according to date
    const sortedData = data.sort((a, b) => Date.parse(a.date_end) > Date.parse(b.date_end) ? -1 : 1);
    const items = sortedData.filter((item) => item.title !== "null");

    const mobileComponent = () => (
        <div>
            {
                items.map((item, index) => {
                    let link = !item.link == '#';
                    return (
                        <div key={index} id={`work${index}`} className="container align-items-center card default-gradient">
                            <div className="row">
                                <img className="work_pic" src={process.env.PUBLIC_URL + '/' + item.image_dir} />
                            </div>
                            <div className="row justify-content-center">
                                <h1 className="title">{item.title}</h1>
                                <p className="description" dangerouslySetInnerHTML={{ __html: item.description }}></p>
                                {link && <a className="description desc_button" href={item.link} target="_blank">前往查看 {">"}</a>}
                            </div>
                        </div>
                    )
                }
                )
            }
        </div>
    );

    const desktopComponent = () => (
        <div>
            {
                items.map((item, index) => {
                    let link = !(item.link == '#');
                    return (
                        <div key={index} id={`work${index}`} className="container card default-gradient">
                            <div className="row align-items-start">
                                <div className="col-md-6 wow bounceInLeft">
                                    <img className="work_pic" src={process.env.PUBLIC_URL + '/' + item.image_dir} loading="lazy" />
                                    <p className="item_description">{`(${item.date_start}-${item.date_end})`}</p>
                                </div>
                                <div className="col-md-6 wow bounceInRight">
                                    <h1 className="title">{item.title}</h1>
                                    <p className="description" dangerouslySetInnerHTML={{ __html: item.description }}></p>
                                    {link && <a className="description desc_button" href={item.link} target="_blank">前往查看 {">"}</a>}
                                </div>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    );

    useEffect(() => {
        function updateResize() {
            if (window.innerWidth < 768)
                setIsMobile(true);
            else setIsMobile(false);
        }
        updateResize();
        window.addEventListener("resize", updateResize);
    }, [])

    return (
        isMobile ? (
            <div>
                {mobileComponent()}
            </div>
        ) : (
            <div>
                {desktopComponent()}
            </div>
        )
    );
}

function Navbar() {
    let location_icon = process.env.PUBLIC_URL + "/img/icon_where.png";
    let logo = process.env.PUBLIC_URL + "/img/nav_logo.png";

    const [isOpen, setIsOpen] = useState(false);
    let buttonHandler = () => setIsOpen(!isOpen);

    // fetch("https://ipinfo.io", {
    //     headers: {
    //         'Content-Type': 'application/json',
    //         'Accept': 'application/json'
    //     }
    // }).then(response => response.json())
    //     .then(data => {
    //         console.log(data);
    //         if (data.status !== 429) {
    //             document.getElementById("location").innerText = data.country;
