import './css/normalize.css';
import './css/bootstrap.min.css';
import './css/mdb.min.css';
import './App.css';
import React, { useState } from 'react';
//import styled from 'styled-components';

async function readJson(dir) {
    try {
        let data = await fetch(dir);
        data = await data.json();
        return data;
    } catch (error) {
        console.log(error);
    }
}

async function Works() {
    let works = "";
    let data = await readJson(require("./json/portfolio.json"));

    //sort date according to date
    data.sort((a, b) => Date.parse(a.date_end) > Date.parse(b.date_end) ? -1 : 1);

    for (let i = 0; i < ndata; i++) {
        let work, link = "";

        if (data[i].link != "#") {
            link = `<a class="description desc_button" href="${data[i].link}" target="_blank">前往查看 ></a>`;
        }

        if (deviceWidth < 768) { //手機界面
            work = `<div id="work${i}" class="container align-items-center card default-gradient">
                        <div class="row">
                            <img class="work_pic" src="${data[i].image_dir}">
                        </div>
                        <div class="row justify-content-center">
                            <h1 class="title">${data[i].title}</h1>
                            <p class="description">${data[i].description}</p>
                            ${link}
                        </div>
                    </div>
                    `
        } else {
            work = `<div id="work${i}" class="container card default-gradient">
                        <div class="row align-items-start">
                            <div class="col-md-6 wow bounceInLeft">
                                <img class="work_pic" src="${data[i].image_dir}" loading="lazy">
                                <p class="item_description">(${data[i].date_start}-${data[i].date_end})</p>
                            </div>
                            <div class="col-md-6 wow bounceInRight">
                                <h1 class="title">${data[i].title}</h1>
                                <p class="description">${data[i].description}</p>
                                ${link}
                            </div>
                        </div>
                    </div>
                    `
        }
        works += work;
    }
}

function Navbar() {
    let location_icon = process.env.PUBLIC_URL + "/img/icon_where.png";
    let logo = process.env.PUBLIC_URL + "/img/nav_logo.png";

    const [isOpen, setIsOpen] = useState(false);
    let buttonHandler = () => setIsOpen(!isOpen);

    // fetch("https://ipinfo.io", {
    //     headers: {
    //         'Content-Type': 'application/json',
    //         'Accept': 'application/json'
    //     }
    // }).then(response => response.json())
    //     .then(data => {
    //         console.log(data);
    //         if (data.status !== 429) {
    //             document.getElementById("location").innerText = data.country;
    //         } else {
    //             document.getElementById("location").innerText = 'TW';
    //         }
    //     });

    return (
        <nav className="navbar navbar-expand-lg navbar-dark navbar-color fixed-top">
            <button className="navbar-toggler third-button" type="button" data-toggle="collapse"
                data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false"
                aria-label="Toggle navigation" onClick={buttonHandler}>
                <div className={isOpen ? "animated-icon2 open" : "animated-icon3"}><span></span><span></span><span></span></div>
            </button>

            <a className="navbar-brand" href={process.env.PUBLIC_URL}><img src={logo} alt="logo" /></a>

            {/* <!--網頁選單--> */}
            <div className={isOpen ? "collapse navbar-collapse show" : "collapse navbar-collapse"} id="navbarSupportedContent">
                <div className="container">
                    <div className="row">
                        {/* <!--top icon--> */}
                        <div className='col'></div>
                        <div className={isOpen ? "d-none" : "col-1 navbar-nav nav-icon"}>
                            <div className="row location">
                                <img className="col" src={location_icon} alt="location" />
                                <p id="location" className="col text-center">TW</p>
                            </div>
                        </div>
                        <div className="w-100"></div>
                        {/* <!--導航選單--> */}
                        <ul className="col navbar-nav ml-auto justify-content-end">
                            <li className="nav-item"><a id="home_link" className="nav-link">首頁</a></li>
                            <li className="nav-item"><a id="portfolio_link" className="nav-link">作品集</a></li>
                            <li className="nav-item"><a id="certificate_link" className="nav-link"
                                href="certificate.html">證書集</a></li>
                            <li className="nav-item"><a id="contact_link" className="nav-link">聯絡方式</a></li>
                        </ul>
                    </div>
                </div>
            </div>
        </nav>
    );
}

function Profile() {

    let PublicURL = process.env.PUBLIC_URL;

    return (
        <div className='container default-gradient'>
            <div className="row profile_area default-bg-color">
                <div className="container row align-items-center profile default-bg-color">
                    <img className="col-md-5 col-8 col-lg-4" src={PublicURL + "/img/portrait.jpg"} />
                    <div className="col-sm-12 col-md-6 col-lg-7 align-items-center profile_detail">
                        <h1 className="row justify-content-center title">Hi! I'm Wayne<br />王偉斌</h1>
                        <p className="row justify-content-center subtitle">國立臺北科技大學<br />資訊工程系</p>
                        <div className="profile_button">
                            <a title="Leetcode" href="https://leetcode.com/hengweibin1898/" target="_blank"><img
                                className="hover_scale" src={PublicURL + "/img/icon_leetcode.png"} /></a>
                            <a title="104履歷" href={PublicURL + "/104.pdf"} target="_blank"><img
                                className="hover_scale" src={PublicURL + "/img/icon_104.png"} /></a>
                            <a title="Github" href="https://github.com/HengWeiBin" target="_blank"><img
                                className="hover_scale" src={PublicURL + "/img/icon_github.png"} /></a>
                            <a title="Facebook" href="https://www.facebook.com/weibin1898/" target="_blank"><img
                                className="hover_scale" src={PublicURL + "/img/icon_facebook.png"} /></a>
                            <a title="Instagram" href="https://www.instagram.com/wei_bin/" target="_blank"><img
                                className="hover_scale" src={PublicURL + "/img/icon_instagram.png"} /></a>
                        </div>
                        <p id="profile_desc" className="description">

                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

function Offset100vh() {
    return (
        <div className="row offset100vh" />
    );
}

export default function App() {
    return (
        <>
            <Navbar />
            <Profile />
            <Offset100vh />
        </>
    );
}
